        Swal.fire({
          title: "Eliminado!",
          text: `${heroe.nombre} ha sido eliminado correctamente`,
          icon: "success"
        });
      }
    });
    }

    cambiar(){
      this.heroesServie.button$.emit('Actualizar')
      console.log('llego a cambiar')
    }


}
