import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { HeroeModel } from 'src/app/models/heroe.model';
import { HeroesService } from 'src/app/services/heroes.service';

import  Swal  from 'sweetalert2'

@Component({
  selector: 'app-heroe',
  templateUrl: './heroe.component.html',
  styleUrls: ['./heroe.component.css']
})
export class HeroeComponent implements OnInit{


  heroe: HeroeModel
  accion = 'Guardar'
  id: any
  constructor(private heroeServices: HeroesService,
              private route: ActivatedRoute){
    this.heroe = {
    nombre:"",
    poder:"",
    vivo: true
  }
  }


  ngOnInit(){
   this.id = this.route.snapshot.paramMap.get('id')

   if (this.id !== 'nuevo'){

  this.heroeServices.getHeroeById(this.id).subscribe( (resp:any) => {
  this.heroe = resp
  this.heroe.id = this.id

  })}
  }

  guardar(form:NgForm){
    if (this.id !== 'nuevo'){

    }else


      if( this.heroe.id){
          this.heroeServices.heroeUpdate(this.heroe).subscribe( resp => {
        Swal.fire({
          title: this.heroe.nombre,
          text: "Se actualizo correctamente",
          timer: 1500,
          icon: "success"
        })
        Swal.showLoading();
      ;
      })

      }
      else{
        this.heroeServices.crearHeroe(this.heroe).subscribe((resp)=>{

          Swal.fire({
            title: "Espere",
            text: "Guardando los datos",
            timer: 1000,
            icon: "info"
          });
        Swal.showLoading();
        })
      }
      form.reset
  }
}
